    super();

    this.cells = cells || [];
  }

  public cell(index: number): Cell {
    return this._cells[index];
  }

  public get cells(): Cell[] {
    return this._cells;
  }

  public set cells(cells: Cell[]) {
    this._cells = cells;
  }

  public toArray(): TCell[] {
    return this.cells.map((cell) => cell.data);
