  ): void {
    this.dispatch('SORT_COLUMN', {
      index: index,
      direction: direction,
      multi: multi,
      compare: compare,
    });
  }

  sortToggle(
    index: number,
    multi?: boolean,
    compare?: Comparator<TCell>,
  ): void {
    this.dispatch('SORT_COLUMN_TOGGLE', {
      index: index,
      multi: multi,
      compare: compare,
    });
  }
}
import {
  PipelineProcessor,
  PipelineProcessorProps,
  ProcessorType,
} from '../processor';
