export class THead extends BaseComponent<THeadProps> {
  private renderColumn(
    column: TColumn,
    rowIndex: number,
    columnIndex: number,
    totalRows: number,
  ): ComponentChild {
    const { rowSpan, colSpan } = calculateRowColSpans(
      column,
      rowIndex,
      totalRows,
    );

    return (
      <TH
        column={column}
        index={columnIndex}
        colSpan={colSpan}
        rowSpan={rowSpan}
      />
    );
  }

  private renderRow(
    row: TColumn[],
    rowIndex: number,
    totalRows: number,
  ): ComponentChild {
    // because the only sortable columns are leaf columns (not parents)
    const leafColumns = Header.leafColumns(this.props.header.columns);

    return (
      <TR>
        {row.map((col) => {
          if (col.hidden) return null;

          return this.renderColumn(
            col,
            rowIndex,
            leafColumns.indexOf(col),
            totalRows,
          );
        })}
      </TR>
    );
  }

  private renderRows(): ComponentChild {
    const rows = Header.tabularFormat(this.props.header.columns);

    return rows.map((row, rowIndex) =>
      this.renderRow(row, rowIndex, rows.length),
    );
  }

  render() {
    if (this.props.header) {
      return (
        <thead
          key={this.props.header.id}
          className={classJoin(className('thead'), this.config.className.thead)}
        >
          {this.renderRows()}
        </thead>
      );
    }

    return null;
  }
}
import { TColumn } from '../types';
import Header from '../header';

export function calculateRowColSpans(
  column: TColumn,
  rowIndex: number,
  totalRows: number,
): { rowSpan: number; colSpan: number } {
  const depth = Header.maximumDepth(column);
  const remainingRows = totalRows - rowIndex;
  const rowSpan = Math.floor(remainingRows - depth - depth / remainingRows);
  const colSpan = (column.columns && column.columns.length) || 1;

  return {
    rowSpan: rowSpan,
    colSpan: colSpan,
  };
}
